INSERT INTO contenido (ID, IDCATEGORIA, NUMEROCONTENIDO, DESCRIPCION) VALUES(210, 4, 19, '¿Cuantos técnicos de Microsoft hacen falta para cambiar una bombilla? Ninguno, es un problema de Hardware.');
