INSERT INTO contenido (ID, IDCATEGORIA, NUMEROCONTENIDO, DESCRIPCION) VALUES(501, 9, 10, 'Este era un chino que tenia su residencia al lado de una iglesia y el cura de la iglesia tenia en su jardin varios perros que todas las noches ladraban hasta que una noche el chino muy molesto se levanto por los ladridos de los perros y exclamo: LOS PELOS DEL CULA NO ME DEJAN DOLMIL');
INSERT INTO contenido (ID, IDCATEGORIA, NUMEROCONTENIDO, DESCRIPCION) VALUES(502, 9, 11, 'Un chino, le dice a curro que se callen los perros pero los perros no se callaban... El chino fue a la policía lo pelos del culo no me dejan dolmil y le dice el policía pues aféiteselos marrano. (El chino hablaba así prque los chinos no pronuncian la r)');
INSERT INTO contenido (ID, IDCATEGORIA, NUMEROCONTENIDO, DESCRIPCION) VALUES(503, 9, 12, 'Dos gitanos jugando al ajedrez y uno le dice al otro: - Jaque mate con el caballo. El otro contesta: - Haaala!!! Y tu ja te mates con la fregoneta!!!.');
