INSERT INTO contenido (ID, IDCATEGORIA, NUMEROCONTENIDO, DESCRIPCION) VALUES(505, 9, 14, 'Dos gallegos suben al ascensor: - ¿Cuál botón aprieto? - El que dice PB, ¿No ves que vamos pabajo?');
