INSERT INTO contenido (ID, IDCATEGORIA, NUMEROCONTENIDO, DESCRIPCION) VALUES(526, 9, 35, 'Un argentino hablando con un amigo. - He salido con esta chica varias veces. Le he llevado los libros, la he llevado al cine, la he invitado a tomar cerveza... ¿Crees que debo besarla.  - No pibe, dice el amigo: Ya has hecho bastante por esa chica.');
INSERT INTO contenido (ID, IDCATEGORIA, NUMEROCONTENIDO, DESCRIPCION) VALUES(527, 9, 36, '¿Por qué los argentinos no se bañan con agua caliente? - Porque se les empaña el espejo.');
INSERT INTO contenido (ID, IDCATEGORIA, NUMEROCONTENIDO, DESCRIPCION) VALUES(528, 9, 37, 'Va caminando un argentino en la playa y pisa una plasta de mierda. Y piensa: ¡Oh, Dios. ¡Qué calor! ¡Me estoy derritiendo!');
