INSERT INTO contenido (ID, IDCATEGORIA, NUMEROCONTENIDO, DESCRIPCION) VALUES(551, 10, 4, '¿Cuál es la forma mas rápida de llegar al corazón de un hombre?... directemente atravez de las costillas.');
INSERT INTO contenido (ID, IDCATEGORIA, NUMEROCONTENIDO, DESCRIPCION) VALUES(552, 10, 5, '¿Por qué solo el 10% de los hombres llegan al cielo?... porque si todos fueran, seria el infierno.');
INSERT INTO contenido (ID, IDCATEGORIA, NUMEROCONTENIDO, DESCRIPCION) VALUES(553, 10, 6, 'Los hombres no maduran hasta que se casan... y después se pudren.');
INSERT INTO contenido (ID, IDCATEGORIA, NUMEROCONTENIDO, DESCRIPCION) VALUES(554, 10, 7, '¿Cómo hacer para que el gordo de tu marido haga abdominales?... una vez tirado en el sofa, ponle el control remoto sobre las rodillas.');
INSERT INTO contenido (ID, IDCATEGORIA, NUMEROCONTENIDO, DESCRIPCION) VALUES(555, 10, 8, '¿Por que los hombres se sientan con las piernas abiertas?... porque si las cierran se apachurran el cerebro.');
