INSERT INTO contenido (ID, IDCATEGORIA, NUMEROCONTENIDO, DESCRIPCION) VALUES(557, 10, 10, 'Las mujeres tienen muchos defectos, los hombres sólo dos: todo lo que hacen y todo lo que dicen.');
INSERT INTO contenido (ID, IDCATEGORIA, NUMEROCONTENIDO, DESCRIPCION) VALUES(558, 10, 11, 'Las mujeres solteras se quejan de que los hombres buenos están casados, las mujeres casadas se quejan de sus maridos. Esto prueba que los hombres buenos no existen.');
INSERT INTO contenido (ID, IDCATEGORIA, NUMEROCONTENIDO, DESCRIPCION) VALUES(559, 10, 12, '¿Por qué se suspendería la práctica de la circuncisión?... porque los médicos temen que pueda causar daño cerebral.');
INSERT INTO contenido (ID, IDCATEGORIA, NUMEROCONTENIDO, DESCRIPCION) VALUES(560, 10, 13, '¿Cómo se llama a un hombre que ha perdido el 99% de sus facultades mentales?... castrado.');
INSERT INTO contenido (ID, IDCATEGORIA, NUMEROCONTENIDO, DESCRIPCION) VALUES(561, 10, 14, '¿Por qué el psicoanálisis es más breve para el hombre que para la mujer?... porque cuando hay que hablar de la infancia, los hombres todavía están allí.');
INSERT INTO contenido (ID, IDCATEGORIA, NUMEROCONTENIDO, DESCRIPCION) VALUES(562, 10, 15, '¿Por qué los hombres prefieren rubias tontas?... porque buscan compañía intelectual.');
