INSERT INTO contenido (ID, IDCATEGORIA, NUMEROCONTENIDO, DESCRIPCION) VALUES(1048, 16, 58, 'Esto es un hombre que tenía un burro y se le cayó a un pozo y le dice el hijo. -¡Papá, papá, el burro se ha caído al pozo! Y responde el padre: - ¡Pues ponle paja porque agua no le falta!');
